$(document).ready(function(){var d=new Date();$("#year").text(d.getFullYear());if($("#contact-form").length)$("#contact-form").validate({rules:{fullname:"required",subject:"required",message:"required",email:{required:!0,email:!0},phone:{required:!0,digits:!0,rangelength:[10,12]}},messages:{fullname:"Please enter your full name",subject:"Please select an option",message:"Please enter your message",email:"Please enter a valid email address",phone:{required:"Please enter your cellphone number",digits:"Please enter a valid cellphone number",rangelength:"Your cellphone number should be 10 digit number"}},submitHandler:function(form,event){event.preventDefault();form.submit();return!1}});if($('.section-services').length)$('.section-services').waypoint(function(direction){if(direction==='down'){$('nav').addClass('sticky')}else{$('nav').removeClass('sticky')}},{offset:'60px'})})